#include<bits\stdc++.h>using namespace std;#define ll long long int#define f(i,k,n) for(int i=k;i<n;i++)int main(){    int t;    cin>>t;    while(t--){        ll i,n,ans=0;        cin>>n;        vector<int> v(105, 0);        f(i,0,n){            ll x;            cin>>x;            ++v[x];        }        f(i,0,102){            if (v[i] <= 1){                ans+=i;                break;            }        }        f(i,0,102){            if (v[i] == 0){                ans+=i;                break;            }        }        cout<<ans<<endl;    }}